sys_ui_page.client_script:
function call_from_pigeon() {
	var callflow = gel("Callflow_list").value;
	if (!isSelected(callflow)) return;
	var guidance = gel("Guidance_list").value;
	if (!isSelected(guidance)) return;

	var sys_id = gel("sysId").value;
	var company = gel("company").value;
	var freetext = document.getElementById("freetext").value;

    var ga = new GlideAjax("x_410824_pegion.SFPigeonUtil");
	ga.addParam("sysparm_name", "executeInvoke");
	ga.addParam("sysparm_incident_sys_id", sys_id);
	ga.addParam("sysparm_callflow", callflow);
	ga.addParam("sysparm_guidance", guidance);
	ga.addParam("sysparm_freetext", freetext);
	ga.getXML(closeDialog);
}

function closeDialog(response) {
	GlideModal.get().destroy();
}

function isSelected(eventName) {
	if (!eventName) {
        document.querySelector('#debugmsg').innerText = 'Select Pigeon Setting.';
        return false;
    }
	return true;
}
